import { useHistory } from "react-router-dom/cjs/react-router-dom";
import { useState } from "react";
import { useSelector } from "react-redux"
import { HOME } from "../utility/Route";
import { registrazione } from "../service/utenteService";
function Registrazione() {


    const emailRegex = /^(([^<>()[\]\\.,;:\s@\"]+(\.[^<>()[\]\\.,;:\s@\"]+)*)|(\".+\"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/
    const passwordRegex = /^(?=.*\d)(?=.*[a-z])(?=.*[A-Z])[0-9a-zA-Z]{8,}$/
    const [utente,setUtente]=useState({
        nome:"",
        cognome:"",
        email:"",
        password:"",
        codiceFiscale:""
    })
    const history = useHistory();
    const [accettoCondizioni, setAccettoCondizioni] = useState(false);
    const [visibile,setVisibile] = useState(false)
    const [messaggio,setMessaggio]= useState("")
  


    const gestisciCheckbox = () => {
        setAccettoCondizioni(!accettoCondizioni);
    };

    function reindirizza(rotta) {
        history.push(rotta)
    }

    function stampa(){
        console.log(utente.nome)
        console.log(utente.cognome)
        console.log(utente.password)

        console.log(utente.email)
        console.log(utente.codiceFiscale)

    }

    function setForm(valore,attributo){
        switch(attributo){
            case 'nome':
                setUtente({...utente,nome:valore})
                console.log(utente.nome)
                break;
            case 'cognome':
                    setUtente({...utente,cognome:valore})
                    break;
            case 'email':
                setUtente({...utente,email:valore})
                break;
            case 'password':
                    setUtente({...utente,password:valore})
                    break;
            case 'codiceFiscale':
                    setUtente({...utente,codiceFiscale:valore})
                    break;
        }
    }

    function validateEmail(email) {
        return email.match(emailRegex);
    }
    function validatePassword(password) {
        return password.match(passwordRegex);
    }

    function validaForm(){
        
        const enableNome = utente.nome !== '' && utente.nome.length >= 3;
        alert(enableNome)
        const enableCognome = utente.cognome !== '' && utente.cognome.length >= 3;
        const enableEmail = utente.email !== '' && validateEmail(utente.email) !== null;
        const enablePassword = utente.password !== '' && validatePassword(utente.password) !== null;
        const enableCodiceFiscale = utente.codiceFiscale !== '' && utente.codiceFiscale.length == 16;

        console.log(enableNome,enableCognome,enableEmail,enablePassword,enableCodiceFiscale)
        if( enableNome && enableCognome && enableEmail && enablePassword && enableCodiceFiscale){
            registrazione(utente).then((response)  =>{
                console.log(response.data)
                {(response.data.success)?
                    <>
                    {setMessaggio("Registrazione effettutata con successo, a breve riceverai una email di conferma attivazione, torna alla home per accedere")}
                    {setVisibile(true)}
                    </>:alert("qualcosa è andato storto")}
            }).catch(error => {
                if (error.response.data) {
                  setVisibile(true)
                  setMessaggio(error.response.data)
                } else {
                  setMessaggio("Errore sconosciuto");
                }
              })
        } else {
            if(!enableNome && !enableCognome && !enableEmail && !enablePassword && !enableCodiceFiscale){
                return <>
                {setMessaggio("Compila i campi correttamente")}
                {setVisibile(true)}
                </>
            }
            if (!enableNome) {    
                return <>
                {setMessaggio("Il nome deve avere una lunghezza minima di 3 caratteri e massimo di 20")}
                {setVisibile(true)}
                </>
            }    
              if (!enableCognome) {
                return <>
                {setMessaggio("Il cognome deve avere una lunghezza minima di 3 caratteri e massimo di 20")}
                {setVisibile(true)}
                </>           
            }  
              if (!enableEmail) {
                return <>
                {setMessaggio("Email non valida")}
                {setVisibile(true)}
                </>
            }    
              if (!enablePassword) {
                return <>
                {setMessaggio("Password non valida, la password deve contenere almeno 8 caratteri di cui un carattere maiuscolo, un carattere minuscolo ed un numero")}
                {setVisibile(true)}
                </>
            }     
              if (!enableCodiceFiscale) {
                return <>
                {setMessaggio("Codice fiscale non valido, lunghezza 16 caratteri")}
                {setVisibile(true)}
                </>
              }
         

        }
    }

    function errorBanner(messaggio){

        if (visibile) {
          setTimeout(() => {
            setVisibile(false);
            setMessaggio("");
          }, 4000);   
          return (
            <div style={{color:"black"}} className="error-banner">
              {messaggio}
            </div>
          );
        } else {
          return null;
        }
      }
    

    return (

        <>
        <form>
            <div className="div">

                <div className="div-container">
                    <h2>Registrazione</h2>
                    <p><b>Inserisci i tuoi dati</b></p>
                    <label style={{ marginBottom: 6 }} for="nome">Nome:</label>
                    <input id="nome"type="text" placeholder="Inserisci il tuo nome" onChange={ (e) => setForm(e.target.value,"nome")} />
                    <label style={{ marginBottom: 6 }} for="nome">Cognome:</label>
                    <input id="cognome"type="text" placeholder="Inserisci il tuo cognome" onChange={ (e) => setForm(e.target.value,"cognome")} />
                    <label style={{ marginBottom: 6 }} for="nome">E-mail:</label>
                    <input id="email"type="text" placeholder="Inserisci la tua email"onChange={ (e) => setForm(e.target.value,"email")} />
                    <label style={{ marginBottom: 6 }} for="nome">Password:</label>
                    <input id="password"type="password" placeholder="Inserisci la password"onChange={ (e) => setForm(e.target.value,"password")} />
                    <label style={{ marginBottom: 6 }} for="nome">Codice fiscale:</label>
                    <input id="codiceFiscale"type="text" placeholder="Inserisci il tuo codice fiscale"onChange={ (e) => setForm(e.target.value,"codiceFiscale")} />
                    <label>
                        <input type="checkbox"
                            name="accettoCondizioni"
                            value={accettoCondizioni}
                            onChange={gestisciCheckbox} />
                        <p style={{ marginLeft: 0, display: "inline" }}> Ho letto e accetto termini e condizioni
                        </p>
                        <a style={{ color: "blue", marginLeft: 10 }} href="">Condizioni e termini.</a>
                    </label>
                    <br />
                    <br />
                    <button type="button" onClick={ ()=> validaForm() }
                        style={{
                            backgroundColor: accettoCondizioni ? 'green' : 'red',
                            color: 'white',
                            cursor: accettoCondizioni ? 'pointer' : 'not-allowed',
                        }}
                        disabled={!accettoCondizioni} >
                        Registrati
                    </button>
                    
                    <button type="button" onClick={() => reindirizza(HOME)} >Torna al menu</button>
                    {errorBanner(messaggio)}





                </div>
            </div>

            </form>




        </>




    )




} export default Registrazione
