import { insertTransazione } from "../service/utenteService";

export const DEPOSITO = "1";
export const PRELIEVO = "2"
export const  BONIFICO = "3"
export const  RICARICA = "4"
export const  BOLLETTINO = "5"
const [visibile, setVisibile] = useState(false)
const [visibile2, setVisibile2] = useState(false)

export function Operazione(tipoOperazione, conto, conti, importo, setImporto, ibanBeneficiario, setMessaggio, setVisibile, setVisibile2, setIbanBeneficiario) {
    const contoAggiornato = { ...conto };
    const tipoDeposito = { id: 1 };
    const tipoPrelievo = { id: 2 };
    const tipoBonifico = { id: 3 };
    const tipoRicarica = { id: 4 };
    const tipoBollettino = { id: 5 };
    let tipo;
    let statoTransazione;
    let contoBeneficiario = null;
    let contoBeneficiarioEsterno = null;

    switch (tipoOperazione) {
        case DEPOSITO:
            tipo = tipoDeposito;
            statoTransazione = { id: 1 };
            break;
        case PRELIEVO:
            tipo = tipoPrelievo;
            statoTransazione = { id: 3 };
            break;
        case BONIFICO:
            tipo = tipoBonifico;
            statoTransazione = { id: 3 };
            contoBeneficiario = conti.find((conti) => conti.numeroConto === ibanBeneficiario);
            break;
        case RICARICA:
            tipo = tipoRicarica;
            statoTransazione = { id: 3 };
            break;
        case BOLLETTINO:
            tipo = tipoBollettino;
            statoTransazione = { id: 3 };
            contoBeneficiarioEsterno = ibanBeneficiario;
            break;
        default:
            setMessaggio("Tipo di operazione non valido");
            setVisibile(true);
            return;
    }

    if (importo <= 0) {
        setMessaggio("Inserisci un importo positivo");
    } else if (importo >= 1000) {
        setMessaggio("Non è possibile effettuare un'operazione superiore a € 1000.00");
    } else if (tipo === tipoBonifico && (!ibanBeneficiario || ibanBeneficiario.length !== 27)) {
        setMessaggio("L'IBAN inserito non è valido");
    } else if (tipo === tipoBonifico && !contoBeneficiario) {
        setMessaggio("Operazione non consentita, inserisci un IBAN valido");
    } else if (importo > conto.saldo) {
        setMessaggio("Operazione non consentita, importo superiore al saldo");
    } else {
        const transazione = {
            conto: { ...conto },
            importo: importo,
            tipo: tipo,
            statoTransazione: statoTransazione,
            contoBeneficiario: contoBeneficiario,
            contoBeneficiarioEsterno: contoBeneficiarioEsterno,
        };
        insertTransazione(transazione)
            .then((response) => {
                if (response.data) {
                    setMessaggio(`La tua richiesta di ${tipoOperazione} è stata presa in carico`);
                    setVisibile2(true);
                    setIbanBeneficiario("");
                    setImporto("");
                }
            })
            .catch((error) => {
                if (error.response && error.response.data) {
                    setMessaggio(error.response.data);
                } else {
                    setMessaggio("Errore sconosciuto");
                }
                setVisibile(true);
                setIbanBeneficiario("");
                setImporto("");
            });
    }
}

export function errorBanner(messaggio) {
    if (visibile) {
        setTimeout(() => {
            setImporto("")
            setVisibile(false);
            setMessaggio("");
        }, 5000);
        return (
            <div style={{ color: "red" }} className="error-banner">
                {messaggio}
            </div>
        );
    } else if (visibile2) {
        setTimeout(() => {
            setImporto("")
            setVisibile2(false);
            setMessaggio("");
        }, 5000);
        return (
            <div style={{ color: "green" }} className="error-banner">
                {messaggio}
            </div>
        );
    } else {
        return null;
    }
}
