import { useState } from "react";
import { useSelector } from "react-redux";
import Deposito from "./Deposito";
import Prelievo from "./Prelievo";
import { INDEX_UTENTE } from "../utility/Route";
import { insertTransazione } from "../service/utenteService";
import { useHistory } from "react-router-dom/cjs/react-router-dom.min";



export default function OperazioniUtente() {


    const conto = useSelector(state => state.conto);
    const [importoDeposito,setImportoDeposito]=useState();
    const [mportoPrelevare,setImportoPrelevare]=useState();
    const history = useHistory();

    const [tipoOperazione,setTipoOperazione] = useState();

    const operazioni = [
        {
          id: 1,
          tipo: "DEPOSITO",
        },
        {
          id: 2,
          tipo: "PRELIEVO",
        },
        {
          id: 3,
          tipo: "BONIFICO",
        }
      ];
    

    function datiConto(conto) {
        if (!conto) {
            return <p>Non hai un conto</p>;
        } else {
            return (
                <>
                    <p style={{ marginBottom: 0 }}>
                        <b>Numero conto:</b> {conto.numeroConto}
                    </p>
                    <p style={{ marginBottom: 0 }}>
                        <b>Stato conto:</b> {(conto.stato) ? <>{conto.stato.statoContoCorrente}</> : <></>}
                    </p>
                    <p style={{ marginBottom: -10 }}>
                        <b>Saldo:</b> € {(conto.saldo) ? <>{conto.saldo.toFixed(2)}</> : <></>}
                    </p>
                </>
            );
        }


    }

    function effettuaDeposito(){
        const contoAggiornato = {...conto}
        const tipo={
            id:1
        }
        const statoTransazione={
            id:1
        }
        const transazione = {
            conto:{...contoAggiornato},
            importo:importoDeposito,
            tipo:tipo,
            statoTransazione:statoTransazione,
        }
        console.log(transazione)
        insertTransazione(transazione).then ((response)=>{
            if(response.data){
                alert("la tua richiesta è stata presa in carico")
                history.push(INDEX_UTENTE)
            }
        }).catch(error =>{
            alert(error.response.data)
          })

    }

    function preleva(){
        if(importoPrelevare){
            const tipo={
                id:2
            }
            const statoTransazione={
                id:3
            }
            const transazione={
                conto:{...conto},
                importo:(importo),
                tipo:tipo,
                statoTransazione:statoTransazione
            }
            insertTransazione(transazione).then((response)=>{
                if(response.data){
                    alert("la tua richiesta è stata presa in carico")
                    history.push(INDEX_UTENTE)
                }
            }).catch(error =>{
                console.log(error)
              })
            
        }else{
            const errore ={
                titoloErrore:"OPERAZIONE NON CONSENTITA",
                descrizioneErrore:"L'importo da prelevare supera il credito disponibile"
            }
            setErrore(errore)
            history.push(ERRORE)
        }
    }


    function operazione(tipoOperazione){

        
        switch(tipoOperazione){
            case '1': 
            return (
                <>
                <input style={{marginTop:10,width:255}} onChange={(e) => setImportoDeposito(e.target.value)} type="number" placeholder="Inserisci l'importo da depositare"></input>
                <button style={{marginTop:20,marginLeft:10,padding:4, width:160}} onClick={ ()=>effettuaDeposito()} type="button" className="">DEPOSITA</button>
                </>
            )
            case '2':
                return (
                    <>
                <input style={{marginTop:10,width:255}} onChange={(e) => setImportoPrelevare(e.target.value)} type="number" placeholder="Inserisci l'importo da prelevare"></input>
                <button style={{marginTop:20,marginLeft:10,padding:4, width:160}} onClick={ ()=>preleva()} type="button" className="">PRELEVA</button>
                </>
                )
        }

    }


    function stampa (){
        console.log(tipoOperazione)
        console.log(operazioni)
    }

    return (
        <>


        
            <div className="div">

                <div  className="div-container">
                    <h3><b>DATI CONTO:</b></h3>
                    <br />
                    <p><b>{datiConto(conto)}</b></p>
                    <p><b>Effettua un operazione</b></p>
                    <select
                        style={{ marginTop: 10, margin: 5 }}
                        onChange={(e) => setTipoOperazione(e.target.value)}
                    >
                        <option value="" >Seleziona un operazione</option>
                        {operazioni ? (
                            <>
                                {operazioni.map((o) => (
                                    <option key={o.id} value={o.id}>
                                        {o.tipo}
                                    </option>
                                ))}
                            </>
                        ) : (
                            <>
                            </>
                        )}
                    </select>

                    {operazione(tipoOperazione)}
                   
                </div>

            </div>







        </>
    )
}
