import { useState, useEffect } from "react"
import { listaConti, listaStatiConto, updateConto } from "../service/adminService"
import axios from "axios";
import { URL_LISTA_CONTI } from "../utility/Endpoint";
import { findConto } from "../service/utenteService";
import { ERRORE, INDEX_ADMIN, LISTA_CONTI } from "../utility/Route";
import { useHistory } from "react-router-dom/cjs/react-router-dom";
import { useSelector } from "react-redux";



export default function ListaContiAdmin({setErrore}) {


    const utente = useSelector(state => state.utente);
    const [conti, setConti] = useState();
    const [stati, setStati] = useState();
    const [id_stato, setId_Stato] = useState();
    const [aggiornamento,setAggiornamento]= useState(true)
    const history = useHistory();

    
    useEffect(() => {

        if(utente.ruolo){
            if (utente.ruolo.id == 1) {

            } else {
                const errore = {
                    titoloErrore: "ACCESSO NON AUTORIZZATO",
                    descrizioneErrore: "Non hai i permessi per visualizzare la pagina"
                }
                setErrore(errore);
                history.push(ERRORE)
            }
        } else {
            const errore = {
                titoloErrore: "UTENTE NON RICONOSCIUTO",
                descrizioneErrore: "Effettua il login"
            }
            setErrore(errore);
            history.push(ERRORE)
        }


        listaConti().then((response) => {
            setConti(response.data)
            setId_Stato("")
        })
        listaStatiConto().then((response) => {
            setStati(response.data)
        })

    }, [aggiornamento])

   

    function modificaStato(conto) {
        conto.stato.id = id_stato
        updateConto(conto).then( (response) => {
            if(response.data){
                setAggiornamento(!aggiornamento)
                alert("Conto numero: "+conto.numero_conto+" aggiornato con successo")   
            }else alert("errore")
            
        })




    }

    return (
        <>
            <div className="table-container">
                <table>
                    <thead>
                        <tr>
                            <th>ID Conto</th>
                            <th>Numero Conto</th>
                            <th>Saldo</th>
                            <th>Stato Conto</th>
                            <th>ID Utente</th>
                            <th>Nome Utente</th>
                            <th>Cognome Utente</th>
                            <th>Codice Fiscale</th>
                            <th>Operazione</th>
                            <th>Conferma</th>
                        </tr>
                    </thead>
                    <tbody>

                        {conti ? (
                            conti.map((conto) => (
                                <tr key={conto.id}>
                                    <td>{conto.id}</td>
                                    <td>{conto.numero_conto}</td>
                                    <td>{conto.saldo}</td>
                                    <td>{conto.stato.stato_conto_corrente}</td>
                                    <td>{conto.utente.id}</td>
                                    <td>{conto.utente.nome}</td>
                                    <td>{conto.utente.cognome}</td>
                                    <td>{conto.utente.codice_fiscale}</td>
                                    <td>
                                        <select
                                            onChange={(e) =>
                                                setId_Stato(e.target.value)} >
                                            <option value="">Seleziona uno stato</option>
                                            {stati ? (
                                                stati.map((stato) => (
                                                    <option key={stato.id} value={stato.id}>
                                                        {stato.stato_conto_corrente}
                                                    </option>
                                                ))
                                            ) : (
                                                <>
                                                </>
                                            )}
                                        </select>
                                    </td>
                                    <td>
                                        <button type="button" onClick={() => modificaStato(conto)}>CONFERMA</button>
                                    </td>
                                </tr>
                            ))
                        ) : (
                            <>
                            </>
                        )}

                    </tbody>
                </table>
                <button className="button-1" style={{ marginTop: 10, height: 40 }}  type="button" onClick={(e) => history.push(INDEX_ADMIN)} >Vai al menu</button>






            </div>

        </>
    )
}
