import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { ERRORE, INDEX_UTENTE } from "../utility/Route";
import { findContoUtente, insertTransazione } from "../service/utenteService";
import { useHistory } from "react-router-dom/cjs/react-router-dom.min";
import { listaConti, listaTipoTransazione } from "../service/adminService";
import { setConto } from "../store/slice/contoSlice";
import { BOLLETTINO, BONIFICO, DEPOSITO, Operazione, PRELIEVO, RICARICA, datiConto, errorBanner } from "../utility/Funzioni";
import { visibil,visibile2, } from "../utility/Funzioni";



export default function OperazioniUtente({ setErrore }) {


    
    const conto = useSelector(state => state.conto);
    const utente = useSelector(state => state.utente);
    const dispatch = useDispatch();
    const history = useHistory();
    const [tipoOperazione, setTipoOperazione] = useState();
    const [conti, setConti] = useState();
    const [listaOperazioni, setListaOperazioni] = useState();
    const [messaggio, setMessaggio] = useState("")
    const [aggiornamento, setAggiornamento] = useState(false)
    const [importo, setImporto] = useState();
    const [ibanBeneficiario, setIbanBeneficiario] = useState();



    useEffect(() => {

        if (utente.ruolo) {
            if (utente.ruolo.id == 2) {

            } else {
                const errore = {
                    titoloErrore: "ACCESSO NON AUTORIZZATO",
                    descrizioneErrore: "Non hai i permessi per visualizzare la pagina"
                }
                setErrore(errore);
                history.push(ERRORE)
            }
        } else {
            const errore = {
                titoloErrore: "UTENTE NON RICONOSCIUTO",
                descrizioneErrore: "Effettua il login"
            }
            setErrore(errore);
            history.push(ERRORE)
        }
        listaConti().then((response) => {
            if (response.data.success) {
                setConti(response.data.data.filter((e) => e.numeroConto != conto.numeroConto))
            }
        }).catch(error => {
            console.error("Qualcosa è andato storto", error)
        })

        listaTipoTransazione().then((response) => {
            if (response.data) {
                setListaOperazioni(response.data)
            }
        }).catch(error => {
            console.error("Qualcosa è andato storto", error)
        })
        if (conto.stato) {
            if (conto.stato.id == 2 || conto.stato.id == 3) {
                const errore = {
                    titoloErrore: "CONTO CHIUSO O DISABILITATO",
                    descrizioneErrore: "Siamo spiacenti ma non puoi effetturare operazioni su questo conto"

                }
                setErrore(errore);
                history.push(ERRORE)
            }
        }
        findContoUtente(utente).then((response) => {
            if (response.data.success) {
                dispatch(setConto(response.data.data))
            } else {
                dispatch(setConto(""))
            }
        }).catch(error => {
            if (error.response.data) {
                setVisibile(true)
                setMessaggio(error.response.data)
            } else {
                setVisibile(true)
                setMessaggio("Errore sconosciuto");
            }
        })

    }, [aggiornamento])





    function transazione() {
        Operazione(
            tipoOperazione, 
            conto,
            conti,
            importo,
            setImporto,
            ibanBeneficiario, 
            setMessaggio, 
            setVisibile, 
            setVisibile2, 
            setIbanBeneficiario
        );
    }


    function operazione(tipoOperazione) {
        switch (tipoOperazione) {
            case DEPOSITO:
                return (
                    <>
                        <br />
                        <label style={{ marginRight: 10 }} for="importo"><b>INSERISCI L'IMPORTO: </b></label>
                        <input style={{ marginTop: 10, width: 255 }} onChange={(e) => setImporto(e.target.value)} value={importo} type="number" placeholder="Inserisci l'importo da depositare"></input>
                        <button style={{ marginTop: 20, marginLeft: 10, padding: 4, width: 160 }} onClick={() => transazione()} type="button" className="">DEPOSITA</button>
                        {errorBanner(messaggio,setMessaggio,setImporto,visibile,visibile2,setVisibile,setVisibile2)}
                    </>
                )
            case PRELIEVO:
                return (
                    <>
                        <br />
                        <label style={{ marginRight: 10 }} for="importo"><b>INSERISCI L'IMPORTO: </b></label>
                        <input style={{ marginTop: 10, width: 255 }} onChange={(e) => setImporto(e.target.value)} value={importo} type="number" placeholder="Inserisci l'importo da prelevare"></input>
                        <button style={{ marginTop: 20, marginLeft: 10, padding: 4, width: 160 }} onClick={() => transazione()} type="button" className="">PRELEVA</button>
                        {errorBanner(messaggio,setMessaggio)}
                    </>
                )
            case BONIFICO:
                return (
                    <>
                        <br />
                        <br />
                        <label for="iban"><b>DIGITA IBAN BENEFICIARIO: </b></label>
                        <input style={{ marginTop: 10, width: 440 }} onChange={(e) => setIbanBeneficiario(e.target.value)} value={ibanBeneficiario} type="text" placeholder="Inserisci IBAN del beneficiario"></input>
                        <br />
                        <p><b>Oppure</b></p>
                        <label for="iban"><b>SELEZIONA IBAN BENEFICIARIO: </b></label>
                        <select
                            style={{ width: 440, height: 40, marginTop: 10, margin: 5 }}
                            onChange={(e) => setIbanBeneficiario(e.target.value)} value={ibanBeneficiario}
                        >
                            <option value=""> &nbsp;Seleziona un IBAN</option>
                            {conti
                                ? conti
                                    .filter((conti) => conti.stato.id === 1)
                                    .map((conti) => (
                                        <option key={conti.numeroConto} value={conti.numeroConto}>
                                            &nbsp;{conti.numeroConto} &nbsp;&nbsp; {conti.utente.nome} &nbsp;&nbsp; {conti.utente.cognome}
                                        </option>
                                    ))
                                : null
                            }
                        </select>
                        <br />
                        <br />

                        <label for="importo"><b>INSERISCI L'IMPORTO: </b></label>
                        <input style={{ marginLeft: 10, marginTop: 0, width: 255 }} onChange={(e) => setImporto(e.target.value)} value={importo} type="number" placeholder="Inserisci l'importo del bonifico"></input>
                        <button style={{ marginTop: 20, marginLeft: 0, padding: 4, width: 160 }} onClick={() => transazione()} type="button" className="">FAI UN BONIFICO</button>
                        {errorBanner(messaggio,setMessaggio,setImporto,visibile,visibile2,setVisibile,setVisibile2)}
                    </>
                )
            case RICARICA:
                return (
                    <>
                        <br />

                        <label style={{ marginTop: 10, marginRight: 10 }} for="importo"><b>INSERISCI IL NUMERO DI TELEFONO: </b></label>
                        <input style={{ marginTop: 10, width: 255 }} type="number" placeholder="Inserisci il numero di telefono"></input>
                        <br />
                        <br />
                        <label style={{ marginRight: 10 }} for="importo"><b>INSERISCI L'IMPORTO: </b></label>
                        <br />
                        <input style={{ marginTop: 10, width: 255 }} onChange={(e) => setImporto(e.target.value)} value={importo} type="number" placeholder="Inserisci l'importo della ricarica"></input>
                        <br />
                        <button style={{ marginTop: 20, marginLeft: 10, padding: 4, width: 160 }} onClick={() => transazione()} type="button" className="">RICARICA</button>
                        {errorBanner(messaggio,setMessaggio,setImporto,visibile,visibile2,setVisibile,setVisibile2)}
                    </>
                )
            case BOLLETTINO:
                return (
                    <>
                        <br />
                        <br />
                        <label for="iban"><b>DIGITA IBAN BENEFICIARIO: </b></label>
                        <input style={{ marginTop: 10, width: 440 }} onChange={(e) => setIbanBeneficiario(e.target.value)} value={ibanBeneficiario} type="text" placeholder="Inserisci IBAN del beneficiario"></input>
                        <br />
                        <br />
                        <br />
                        <label for="importo"><b>INSERISCI L'IMPORTO: </b></label>
                        <input style={{ marginLeft: 10, marginTop: 0, width: 255 }} onChange={(e) => setImporto(e.target.value)} value={importo} type="number" placeholder="Inserisci l'importo del bonifico"></input>
                        <button style={{ marginTop: 20, marginLeft: 0, padding: 4, width: 160 }} onClick={() => transazione()} type="button" className="">FAI UN BONIFICO</button>
                        {errorBanner(messaggio,setMessaggio,setImporto,visibile,visibile2,setVisibile,setVisibile2)}
                    </>
                )

        }

    }




    return (
        <>
            <div className="div">
                <div className="div-container">
                    <h3><b>DATI CONTO:</b></h3>
                    <br />
                    <p><b>{datiConto(conto)}</b></p>
                    <p><b>Effettua un operazione</b></p>
                    <select
                        style={{ marginTop: 10, margin: 5 }}
                        onChange={(e) => setTipoOperazione(e.target.value)}
                    >
                        <option value="" >Seleziona un operazione</option>
                        {listaOperazioni ? (
                            <>
                                {listaOperazioni.map((o) => (
                                    <option key={o.id} value={o.id}>
                                        {o.tipoTransazione}
                                    </option>
                                ))}
                            </>
                        ) : (
                            <>
                            </>
                        )}
                    </select>
                    {operazione(tipoOperazione)}
                </div>
            </div>
        </>
    )
}
