import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import Deposito from "./Deposito";
import Prelievo from "./Prelievo";
import { ERRORE, INDEX_UTENTE } from "../utility/Route";
import { findContoUtente, insertTransazione } from "../service/utenteService";
import { useHistory } from "react-router-dom/cjs/react-router-dom.min";
import { listaConti, listaTipoTransazione } from "../service/adminService";
import { setConto } from "../store/slice/contoSlice";



export default function OperazioniUtente({ setErrore }) {


    const DEPOSITO = "1"
    const PRELIEVO = "2"
    const BONIFICO = "3" 
    const RICARICA = "4"
    const BOLLETTINO = "5"
    const conto = useSelector(state => state.conto);
    const utente = useSelector(state => state.utente);
    const dispatch = useDispatch();
    const history = useHistory();
    const [tipoOperazione, setTipoOperazione] = useState();
    const [conti, setConti] = useState();
    const [listaOperazioni,setListaOperazioni] = useState();
    const [visibile,setVisibile] = useState(false)
    const [visibile2,setVisibile2] = useState(false)
    const [messaggio,setMessaggio]= useState("")
    const [aggiornamento,setAggiornamento] = useState(false)
    const [importo,setImporto] = useState();
    const [ibanBeneficiario, setIbanBeneficiario] = useState();



    useEffect(() => {

        if (utente.ruolo) {
            if (utente.ruolo.id == 2) {

            } else {
                const errore = {
                    titoloErrore: "ACCESSO NON AUTORIZZATO",
                    descrizioneErrore: "Non hai i permessi per visualizzare la pagina"
                }
                setErrore(errore);
                history.push(ERRORE)
            }
        } else {
            const errore = {
                titoloErrore: "UTENTE NON RICONOSCIUTO",
                descrizioneErrore: "Effettua il login"
            }
            setErrore(errore);
            history.push(ERRORE)
        }




        listaConti().then((response) => {
            if (response.data.success) {
                setConti(response.data.data.filter((e) =>e.numeroConto != conto.numeroConto))
            }
        }).catch(error => {
            console.error("Qualcosa è andato storto",error)
           })

        listaTipoTransazione().then((response)=>{
        if(response.data){
            setListaOperazioni(response.data)
        }
        }).catch(error => {
           console.error("Qualcosa è andato storto",error)
          })




        if (conto.stato) {
            if (conto.stato.id == 2 || conto.stato.id == 3) {
                const errore = {
                    titoloErrore: "CONTO CHIUSO O DISABILITATO",
                    descrizioneErrore: "Siamo spiacenti ma non puoi effetturare operazioni su questo conto"

                }
                setErrore(errore);
                history.push(ERRORE)
            }
        }

        
        findContoUtente(utente).then((response) => {
            if (response.data.success) {
                dispatch(setConto(response.data.data))
            }else{
                dispatch(setConto(""))
            }
        }).catch(error => {
            if (error.response.data) {
              setVisibile(true)
              setMessaggio(error.response.data)
            } else {
              setVisibile(true)
              setMessaggio("Errore sconosciuto");
            }
          })

    }, [aggiornamento])


    function datiConto(conto) {
        if (!conto) {
            return <p>Non hai un conto</p>;
        } else {
            return (
                <>
                    <p style={{ marginBottom: 0 }}>
                        <b>Numero conto:</b> {conto.numeroConto}
                    </p>
                    <p style={{ marginBottom: 0 }}>
                        <b>Stato conto:</b> {(conto.stato) ? <>{conto.stato.statoContoCorrente}</> : <></>}
                    </p>
                    <p style={{ marginBottom: -10 }}>
                        <b>Saldo:</b> € {(conto.saldo) ? <>{conto.saldo.toFixed(2)}</> : <></>}
                    </p>
                </>
            );
        }


    }


    function transazione (){
        switch (tipoOperazione){
            case DEPOSITO:{
                const contoAggiornato = {...conto}
                const tipo={
                    id:1
                }
                const statoTransazione={
                    id:1
                }    
                const transazione = {
                    conto:{...contoAggiornato},
                    importo:importo,
                    tipo:tipo,
                    statoTransazione:statoTransazione,
                }
                if(importo<1000){
                    insertTransazione(transazione).then ((response)=>{
                        if(response.data){
                            setVisibile2(true)
                            setAggiornamento(!aggiornamento)
                            setImporto("")
                            const importoNumero = parseFloat(importo)
                            const importoFormattato = importoNumero.toFixed(2);
                            setMessaggio("Hai depositato un importo di € "+importoFormattato+" sul tuo conto numero "+conto.numeroConto)
                        }
                    }).catch(error => {
                        if(error.response){
                            if (error.response.data) {
                                setVisibile(true)
                                setMessaggio(error.response.data)
                            }
                        }
                        else {
                        setMessaggio("Errore sconosciuto");
                        }
                    }) 
                }else{
                    setMessaggio("Non è possibile effettuare depositi sopra € 1000.00")
                    setAggiornamento(!aggiornamento)
                    setVisibile(true)
                    setImporto("")
                }
                break;
            }
            case PRELIEVO:{
                if(importo<1000){
                    if(importo>0){
                        if(importo<conto.saldo){
                            const tipo={
                                id:2
                            }
                            const statoTransazione={
                                id:3
                            }       
                            const transazione={
                                conto:{...conto},
                                importo:(importo),
                                tipo:tipo,
                                statoTransazione:statoTransazione,
                            }
                            insertTransazione(transazione).then((response)=>{
                                if(response.data){
                                    setVisibile2(true)
                                    setImporto("")
                                    setMessaggio("La tua richiesta di prelievo è stata presa in carico")
                                    }
                                }).catch(error => {
                                    if (error.response.data) {
                                      setVisibile(true)
                                      setMessaggio(error.response.data)
                                    } else {
                                      setMessaggio("Errore sconosciuto");
                                    }
                                  })
                        }else{
                            setMessaggio("Operazione non consentita, importo superiore al saldo")
                            setVisibile(true)
                            setImporto("") 
                        }      
                    }else{
                        setMessaggio("Inserisci un importo positivo")
                        setVisibile(true)
                        setImporto("")
                    }        
                }else{
                    setMessaggio("Non è possibile richiedere un prelievo superiore a € 1000.00")
                    setVisibile(true)
                    setImporto("")
                }
                break;
            }
            case BONIFICO:{
                if(importo>0){
                    if (importo <1000) {
                        if(importo< conto.saldo){
                            if(ibanBeneficiario){
                                if(ibanBeneficiario.length ==27){
                                    const statoTransazione = {
                                        id: 3
                                    }
                                    const tipoTransazione = {
                                        id: 3
                                    }
                                    //QUI MI PRENDO IL CONTO RICICLANDOMI LA LISTA, PASSO IL CONTO ALLA TRANSAZIONE E FACCIO LA CHIAMATA PER INSERT TRANSAZIONE
                                    const contoBeneficiario = conti.find((conti) => conti.numeroConto === ibanBeneficiario)   //CON IL FIND PRENDI UN OGGETTO DELLA LISTA DOVE LA CONDIZIONE SI VERIFICA
                                    // CON IL FILTER PRENDI UNA LISTA DI OGGETTI DOVE LA CONDIZIONE SI VERIFICA
                        
                                    const transazione = {
                                        conto: { ...conto },
                                        importo: (importo),
                                        tipo: tipoTransazione,
                                        statoTransazione: statoTransazione,
                                        contoBeneficiario: contoBeneficiario,
                                    }
                                    insertTransazione(transazione).then((response) => {
                                        if (response.data) {
                                            const impor = parseFloat(importo)
                                            setMessaggio("La tua richiesta di bonifico di € "+impor.toFixed(2)+" è stata presa in carico");
                                            setVisibile2(true)
                                            setIbanBeneficiario("")
                                            setImporto("")
                                        }
                                    }).catch(error => {
                                        alert("ok catch")
                                        if (error.response.data) {
                                          setVisibile(true)
                                          setMessaggio(error.response.data)
                                          setIbanBeneficiario("")
                                          setImporto("")
                                        } else {
                                          setMessaggio("Errore sconosciuto");
                                          setIbanBeneficiario("")
                                          setImporto("")
                                        }
                                      }).catch(error => {
                                        console.log(error)
                                    })
                                }else{
                                setMessaggio("L'iban inserito non è valido");
                                setVisibile(true)
                                setIbanBeneficiario("")
                                setImporto("")
                                }
                            }else{
                            setMessaggio("Operazione non consentita, inserisci l'iban");
                            setVisibile(true)
                            setIbanBeneficiario("")
                            setImporto("")
                            }
                            
                        }else{
                        setMessaggio("L'importo inserito è superiore al saldo");
                        setVisibile(true)
                        setIbanBeneficiario("")
                        setImporto("")
                    } 
                    }else{     
                        setMessaggio("Operazione non consentita, l'importo è superiore ad € 1000.00");
                        setVisibile(true)
                        setIbanBeneficiario("")
                        setImporto("")
                    } 
                }else{
                    setMessaggio("Operazione non consentita, inserisci un importo positivo");
                    setVisibile(true)
                    setIbanBeneficiario("")
                    setImporto("")
                }
                break;
            }
            case RICARICA:{
                if(importo<1000){
                    if(importo>0){
                        if(importo<conto.saldo){
                            const tipo={
                                id:4
                            }
                            const statoTransazione={
                                id:3
                            }       
                            const transazione={
                                conto:{...conto},
                                importo:(importo),
                                tipo:tipo,
                                statoTransazione:statoTransazione,
                            }
                            insertTransazione(transazione).then((response)=>{
                                if(response.data){
                                    setVisibile2(true)
                                    setImporto("")
                                    setMessaggio("La tua richiesta di prelievo è stata presa in carico")
                                    }
                                }).catch(error => {
                                    if (error.response.data) {
                                      setVisibile(true)
                                      setMessaggio(error.response.data)
                                    } else {
                                      setMessaggio("Errore sconosciuto");
                                    }
                                  })
                        }else{
                            setMessaggio("Operazione non consentita, importo superiore al saldo")
                            setVisibile(true)
                            setImporto("") 
                        }      
                    }else{
                        setMessaggio("Inserisci un importo positivo")
                        setVisibile(true)
                        setImporto("")
                    }        
                }else{
                    setMessaggio("Non è possibile richiedere un prelievo superiore a € 1000.00")
                    setVisibile(true)
                    setImporto("")
                }
                break;
            }
            case BOLLETTINO:{
                if(importo>0){
                    if (importo <1000) {
                        if(importo< conto.saldo){
                            if(ibanBeneficiario){
                                if(ibanBeneficiario.length ==27){
                                    const tipo={
                                        id:5
                                    }
                                    const statoTransazione={
                                        id:3
                                    }       
                                    const transazione={
                                        conto:{...conto},
                                        importo:(importo),
                                        tipo:tipo,
                                        statoTransazione:statoTransazione,
                                        contoBeneficiarioEsterno: ibanBeneficiario
                                    }
                                    insertTransazione(transazione).then((response)=>{
                                        if(response.data){
                                            setVisibile2(true)
                                            setImporto("")
                                            setMessaggio("La tua richiesta di prelievo è stata presa in carico")
                                            }
                                        }).catch(error => {
                                            if (error.response.data) {
                                              setVisibile(true)
                                              setMessaggio(error.response.data)
                                            } else {
                                              setMessaggio("Errore sconosciuto");
                                            }
                                          })
                                }else{
                                setMessaggio("L'iban inserito non è valido");
                                setVisibile(true)
                                setIbanBeneficiario("")
                                setImporto("")
                                }
                            }else{
                            setMessaggio("Operazione non consentita, inserisci l'iban");
                            setVisibile(true)
                            setIbanBeneficiario("")
                            setImporto("")
                            }
                            
                        }else{
                        setMessaggio("L'importo inserito è superiore al saldo");
                        setVisibile(true)
                        setIbanBeneficiario("")
                        setImporto("")
                    } 
                    }else{     
                        setMessaggio("Operazione non consentita, l'importo è superiore ad € 1000.00");
                        setVisibile(true)
                        setIbanBeneficiario("")
                        setImporto("")
                    } 
                }else{
                    setMessaggio("Operazione non consentita, inserisci un importo positivo");
                    setVisibile(true)
                    setIbanBeneficiario("")
                    setImporto("")
                }
                break;
            }
           

        }
    }

    function errorBanner(messaggio) {
        if (visibile) {
          setTimeout(() => {
            setImporto("")
            setVisibile(false);
            setMessaggio("");
          }, 5000);
          return (
            <div style={{ color: "red" }} className="error-banner">
              {messaggio}
            </div>
          );
        } else if (visibile2) {
          setTimeout(() => {
            setImporto("")
            setVisibile2(false);
            setMessaggio("");
          }, 5000);
          return (
            <div style={{ color: "green" }} className="error-banner">
              {messaggio}
            </div>
          );
        } else {
          return null;
        }
      }


    function operazione(tipoOperazione) {
        switch (tipoOperazione) {
            case DEPOSITO:
                return (
                    <>
                    <br />
                        <label style={{marginRight:10}} for="importo"><b>INSERISCI L'IMPORTO: </b></label>
                        <input style={{ marginTop: 10, width: 255 }} onChange={(e) => setImporto(e.target.value)} value={importo} type="number" placeholder="Inserisci l'importo da depositare"></input>
                        <button style={{ marginTop: 20, marginLeft: 10, padding: 4, width: 160 }} onClick={() => transazione()} type="button" className="">DEPOSITA</button>
                        {errorBanner(messaggio)}
                    </>
                )
            case PRELIEVO:
                return (
                    <>
                        <br />
                        <label style={{marginRight:10}} for="importo"><b>INSERISCI L'IMPORTO: </b></label>
                        <input style={{ marginTop: 10, width: 255 }} onChange={(e) => setImporto(e.target.value)} value={importo} type="number" placeholder="Inserisci l'importo da prelevare"></input>
                        <button style={{ marginTop: 20, marginLeft: 10, padding: 4, width: 160 }} onClick={() => transazione()} type="button" className="">PRELEVA</button>
                        {errorBanner(messaggio)}
                    </>
                )
            case BONIFICO:
                return (
                    <>
                    <br />
                    <br />
                         <label for="iban"><b>DIGITA IBAN BENEFICIARIO: </b></label>
                        <input style={{ marginTop: 10, width: 440 }} onChange={(e) => setIbanBeneficiario(e.target.value)} value={ibanBeneficiario} type="text" placeholder="Inserisci IBAN del beneficiario"></input>
                        <br />
                        <p><b>Oppure</b></p>
                        <label for="iban"><b>SELEZIONA IBAN BENEFICIARIO: </b></label>
                        <select
                            style={{ width: 440, height: 40, marginTop: 10, margin: 5 }}
                            onChange={(e) => setIbanBeneficiario(e.target.value)} value={ibanBeneficiario}
                        >
                            <option value=""> &nbsp;Seleziona un IBAN</option>
                            {conti
                                ? conti
                                    .filter((conti) => conti.stato.id === 1)
                                    .map((conti) => (
                                        <option key={conti.numeroConto} value={conti.numeroConto}>
                                            &nbsp;{conti.numeroConto} &nbsp;&nbsp; {conti.utente.nome} &nbsp;&nbsp; {conti.utente.cognome}
                                        </option>
                                    ))
                                : null // Aggiungi null se iban non è definito al momento
                            }
                        </select>
                        <br />
                        <br />

                        <label for="importo"><b>INSERISCI L'IMPORTO: </b></label>
                        <input style={{marginLeft:10, marginTop: 0, width: 255 }} onChange={(e) => setImporto(e.target.value)} value={importo} type="number" placeholder="Inserisci l'importo del bonifico"></input>
                        <button style={{ marginTop: 20, marginLeft: 0, padding: 4, width: 160 }} onClick={() => transazione()} type="button" className="">FAI UN BONIFICO</button>
                        {errorBanner(messaggio)}
                    </>
                )
                case RICARICA:
                return (
                    <>
                        <br />
                        
                        <label style={{marginTop: 10,marginRight:10}} for="importo"><b>INSERISCI IL NUMERO DI TELEFONO: </b></label>
                        <input style={{ marginTop: 10, width: 255 }} type="number" placeholder="Inserisci il numero di telefono"></input>
                        <br />
                        <br />
                        <label style={{marginRight:10}} for="importo"><b>INSERISCI L'IMPORTO: </b></label>
                        <br />
                        <input style={{ marginTop: 10, width: 255 }} onChange={(e) => setImporto(e.target.value)} value={importo} type="number" placeholder="Inserisci l'importo della ricarica"></input>
                        <br />
                        <button style={{ marginTop: 20, marginLeft: 10, padding: 4, width: 160 }} onClick={() => transazione()} type="button" className="">RICARICA</button>
                        {errorBanner(messaggio)}
                    </>
                )
                case BOLLETTINO:
                return (
                    <>
                    <br />
                    <br />
                         <label for="iban"><b>DIGITA IBAN BENEFICIARIO: </b></label>
                        <input style={{ marginTop: 10, width: 440 }} onChange={(e) => setIbanBeneficiario(e.target.value)} value={ibanBeneficiario} type="text" placeholder="Inserisci IBAN del beneficiario"></input>
                        <br />
                        <br />
                        <br />
                        <label for="importo"><b>INSERISCI L'IMPORTO: </b></label>
                        <input style={{marginLeft:10, marginTop: 0, width: 255 }} onChange={(e) => setImporto(e.target.value)} value={importo} type="number" placeholder="Inserisci l'importo del bonifico"></input>
                        <button style={{ marginTop: 20, marginLeft: 0, padding: 4, width: 160 }} onClick={() => transazione()} type="button" className="">FAI UN BONIFICO</button>
                        {errorBanner(messaggio)}
                    </>
                )

        }

    }




    return (
        <>



            <div className="div">

                <div className="div-container">
                    <h3><b>DATI CONTO:</b></h3>
                    <br />
                    <p><b>{datiConto(conto)}</b></p>
                    <p><b>Effettua un operazione</b></p>
                    <select
                        style={{ marginTop: 10, margin: 5 }}
                        onChange={(e) => setTipoOperazione(e.target.value)}
                    >
                        <option value="" >Seleziona un operazione</option>
                        {listaOperazioni ? (
                            <>
                                {listaOperazioni.map((o) => (
                                    <option key={o.id} value={o.id}>
                                        {o.tipoTransazione}
                                    </option>
                                ))}
                            </>
                        ) : (
                            <>
                            </>
                        )}
                    </select>

                    {operazione(tipoOperazione)}

                </div>

            </div>







        </>
    )
}
