import { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import Deposito from "./Deposito";
import Prelievo from "./Prelievo";
import { ERRORE, INDEX_UTENTE } from "../utility/Route";
import { insertTransazione } from "../service/utenteService";
import { useHistory } from "react-router-dom/cjs/react-router-dom.min";
import { listaConti, listaTipoTransazione } from "../service/adminService";



export default function OperazioniUtente({ setErrore }) {


    const DEPOSITO = "1"
    const PRELIEVO = "2"
    const BONIFICO = "3" 
    const RICARICA = "4"
    const BOLLETTINO = "5"
    const conto = useSelector(state => state.conto);
    const [importoDeposito, setImportoDeposito] = useState();
    const [importoPrelevare, setImportoPrelevare] = useState();
    const [importoBonifico, setImportoBonifico] = useState();
    const history = useHistory();
    const [contoBenef, setContoBenef] = useState();
    const [tipoOperazione, setTipoOperazione] = useState();
    const [conti, setConti] = useState();
    const [listaOperazioni,setListaOperazioni] = useState();
    const [visibile,setVisibile] = useState(false)
    const [messaggio,setMessaggio]= useState("")
    const [aggiornamento,setAggiornamento] = useState(false)
    const [importo,setImporto] = useState();



    useEffect(() => {
        listaConti().then((response) => {
            if (response.data.success) {
                setConti(response.data.data.filter((e) =>e.numeroConto != conto.numeroConto))
            }
        }).catch(error => {
            console.error("Qualcosa è andato storto",error)
           })

        listaTipoTransazione().then((response)=>{
        if(response.data){
            setListaOperazioni(response.data)
        }
        }).catch(error => {
           console.error("Qualcosa è andato storto",error)
          })




        if (conto.stato) {
            if (conto.stato.id == 2 || conto.stato.id == 3) {
                const errore = {
                    titoloErrore: "CONTO CHIUSO O DISABILITATO",
                    descrizioneErrore: "Siamo spiacenti ma non puoi effetturare operazioni su questo conto"

                }
                setErrore(errore);
                history.push(ERRORE)
            }
        }


    }, [])


    function datiConto(conto) {
        if (!conto) {
            return <p>Non hai un conto</p>;
        } else {
            return (
                <>
                    <p style={{ marginBottom: 0 }}>
                        <b>Numero conto:</b> {conto.numeroConto}
                    </p>
                    <p style={{ marginBottom: 0 }}>
                        <b>Stato conto:</b> {(conto.stato) ? <>{conto.stato.statoContoCorrente}</> : <></>}
                    </p>
                    <p style={{ marginBottom: -10 }}>
                        <b>Saldo:</b> € {(conto.saldo) ? <>{conto.saldo.toFixed(2)}</> : <></>}
                    </p>
                </>
            );
        }


    }

    function effettuaDeposito() {
        const contoAggiornato = { ...conto }
        const tipo = {
            id: 1
        }
        const statoTransazione = {
            id: 1
        }
        const transazione = {
            conto: { ...contoAggiornato },
            importo: importoDeposito,
            tipo: tipo,
            statoTransazione: statoTransazione,
        }
        console.log(transazione)
        insertTransazione(transazione).then((response) => {
            if (response.data) {
                alert("la tua richiesta è stata presa in carico")
                history.push(INDEX_UTENTE)
            }
        }).catch(error => {
            alert(error.response.data)
        })

    }

    function preleva() {
        if (importoPrelevare) {
            const tipo = {
                id: 2
            }
            const statoTransazione = {
                id: 3
            }
            const transazione = {
                conto: { ...conto },
                importo: (importoPrelevare),
                tipo: tipo,
                statoTransazione: statoTransazione
            }
            insertTransazione(transazione).then((response) => {
                if (response.data) {
                    alert("la tua richiesta è stata presa in carico")
                    history.push(INDEX_UTENTE)
                }
            }).catch(error => {
                console.log(error)
            })

        } else {
            const errore = {
                titoloErrore: "OPERAZIONE NON CONSENTITA",
                descrizioneErrore: "L'importo da prelevare supera il credito disponibile"
            }
            setErrore(errore)
            history.push(ERRORE)
        }
    }

    function bonifico(){
        if(importoBonifico<conto.saldo){
            const statoTransazione={
                id:3
            }
            const tipoTransazione={
                id:3
            }
            //QUI MI PRENDO IL CONTO RICICLANDOMI LA LISTA, PASSO IL CONTO ALLA TRANSAZIONE E FACCIO LA CHIAMATA PER INSERT TRANSAZIONE
            const contoB = conti.find((conti)=>conti.numeroConto === contoBenef)   //CON IL FIND PRENDI UN OGGETTO DELLA LISTA DOVE LA CONDIZIONE SI VERIFICA
                                                                                                // CON IL FILTER PRENDI UNA LISTA DI OGGETTI DOVE LA CONDIZIONE SI VERIFICA

            const transazione = {
                conto:{...conto},
                importo: importoBonifico,
                tipo:tipoTransazione,
                statoTransazione:statoTransazione,
                contoBeneficiario:contoB,
            }
            insertTransazione(transazione).then((response)=>{
                if(response.data) {
                    alert("la tua richiesta è stata presa in carico")
                }
            }).catch(error => {
                console.log(error)
            })

        }else{
            alert("importo superiore al saldo conto")
        }
    }

    function transazione (){
        switch (tipoOperazione){

            case DEPOSITO:{
                const contoAggiornato = {...conto}
                const tipo={
                    id:1
                }
                const statoTransazione={
                    id:1
                }    
                const transazione = {
                    conto:{...contoAggiornato},
                    importo:importo,
                    tipo:tipo,
                    statoTransazione:statoTransazione,
                }
                if(importo<1000){
                    insertTransazione(transazione).then ((response)=>{
                        if(response.data){
                            setVisibile(true)
                            setAggiornamento(!aggiornamento)
                            setImporto("")
                            const importoNumero = parseFloat(importo)
                            const importoFormattato = importoNumero.toFixed(2);
                            setMessaggio("Hai depositato un importo di € "+importoFormattato+" sul tuo conto numero "+conto.numeroConto)
                        }
                    }).catch(error => {
                        if(error.response){
                            if (error.response.data) {
                                setVisibile(true)
                                setMessaggio(error.response.data)
                            }
                        }
                        else {
                        setMessaggio("Errore sconosciuto");
                        }
                    }) 
                }else{
                    setMessaggio("Non è possibile effettuare depositi sopra € 1000.00")
                    setAggiornamento(!aggiornamento)
                    setVisibile(true)
                    setImporto("")
                }
                break;
            }
            case PRELIEVO:{
                break;
            }
            case BONIFICO:{
                break;
            }
            case RICARICA:{
                break;
            }
            case BOLLETTINO:{
                break;
            }
           

        }
    }





    function operazione(tipoOperazione) {


        switch (tipoOperazione) {
            case DEPOSITO:
                return (
                    <>
                    <br />
                        <label style={{marginLeft:0}} for="importo"><b>INSERISCI L'IMPORTO: </b></label>
                        <input style={{ marginTop: 10, width: 255 }} onChange={(e) => setImporto(e.target.value)} value={importo} type="number" placeholder="Inserisci l'importo da depositare"></input>
                        <button style={{ marginTop: 20, marginLeft: 10, padding: 4, width: 160 }} onClick={() => transazione()} type="button" className="">DEPOSITA</button>
                    </>
                )
            case PRELIEVO:
                return (
                    <>
                        <input style={{ marginTop: 10, width: 255 }} onChange={(e) => setImportoPrelevare(e.target.value)} type="number" placeholder="Inserisci l'importo da prelevare"></input>
                        <button style={{ marginTop: 20, marginLeft: 10, padding: 4, width: 160 }} onClick={() => preleva()} type="button" className="">PRELEVA</button>
                    </>
                )
            case BONIFICO:
                return (
                    <>
                        <input style={{ marginTop: 10, width: 255 }} onChange={(e) => setContoBenef(e.target.value)} type="text" placeholder="Inserisci IBAN del beneficiario"></input>
                        <br />
                        <p><b>Oppure</b></p>
                        <select
                            style={{ width: 400, height: 40, marginTop: 10, margin: 5 }}
                            onChange={(e) => setContoBenef(e.target.value)}
                        >
                            <option value=""> &nbsp;Seleziona un IBAN</option>
                            {conti
                                .filter((conti) => conti.stato.id === 1) // Filtra gli IBAN con stato.id === 1
                                .map((conti) => (
                                    <option key={conti.numeroConto} value={conti.numeroConto}>
                                        &nbsp;{conti.numeroConto} &nbsp;&nbsp; {conti.utente.nome} &nbsp;&nbsp; {conti.utente.cognome}
                                    </option>
                                ))}
                        </select>
                        <br />
                        <input style={{ marginTop: 10, width: 255 }} onChange={(e) => setImportoBonifico(e.target.value)} type="number" placeholder="Inserisci l'importo del bonifico"></input>
                        <button style={{ marginTop: 20, marginLeft: 10, padding: 4, width: 160 }} onClick={() => bonifico()} type="button" className="">FAI UN BONIFICO</button>
                    </>
                )
        }

    }




    return (
        <>



            <div className="div">

                <div className="div-container">
                    <h3><b>DATI CONTO:</b></h3>
                    <br />
                    <p><b>{datiConto(conto)}</b></p>
                    <p><b>Effettua un operazione</b></p>
                    <select
                        style={{ marginTop: 10, margin: 5 }}
                        onChange={(e) => setTipoOperazione(e.target.value)}
                    >
                        <option value="" >Seleziona un operazione</option>
                        {listaOperazioni ? (
                            <>
                                {listaOperazioni.map((o) => (
                                    <option key={o.id} value={o.id}>
                                        {o.tipoTransazione}
                                    </option>
                                ))}
                            </>
                        ) : (
                            <>
                            </>
                        )}
                    </select>

                    {operazione(tipoOperazione)}

                </div>

            </div>







        </>
    )
}
