import { useDispatch, useSelector } from "react-redux";
import "../navbar.css";
import { APRI_CONTO, DEPOSITO, HOME, LISTA_TRANSAZIONI_UTENTE, PRELIEVO } from "../utility/Route";
import { resetConto } from "../store/slice/contoSlice";
import { resetUtente } from "../store/slice/utenteSlice";
import { useHistory } from "react-router-dom/cjs/react-router-dom";


export default function Navbar({paginaCorrente}){

    const utente = useSelector(state => state.utente);
    const conto = useSelector(state => state.conto);
    const dispatch = useDispatch();
    const history = useHistory();



    function apriConto(rotta) {
        if (!conto) {
            history.push(rotta)
        } else alert("hai gia un conto")
    }

    function logout() {
        dispatch(resetUtente())
        dispatch(resetConto())
        history.push(HOME)
    }





    function navBar(){
        return (
            (utente.nome) ? (
                <>
                <p style={{fontSize:30,display:"inline-block",marginLeft:100}}>{utente.nome} {utente.cognome}</p>
                <button  style={{marginTop:10,marginLeft:180,display:"inline-block",width:100}} type="button" id="logout" onClick={() => logout()}><b>Logout</b></button>
                </>
                ):null
        )
        
    }


    function visualSideBar(paginaCorrente){

        if (paginaCorrente === "indexUtente" || paginaCorrente === "indexAdmin") {
            return null;
          }
        
    return(
      
        
        (utente.ruolo) ?
        
        (utente.ruolo.id==1) ? (
            <div class="sidebar">
            <h1>CIAO ADMIN</h1>
            </div>
        ) : (
            <div class="sidebar">
            <p><b>Operazioni disponibili</b></p>
                    <button type="button" onClick={() => history.push(DEPOSITO)}>Fai un deposito</button>
                    <button type="button" onClick={() => history.push(PRELIEVO)}>Effettua un prelievo</button>
                    <button type="button">Effettua un bonifico</button>
                    <button type="button" onClick={()=>history.push(LISTA_TRANSAZIONI_UTENTE)}>Lista transazioni</button>
                    <button type="button" onClick={() => apriConto(APRI_CONTO)}>Apri conto</button>
            </div>  
        ): null
    )
        
    }


return(
    <>
    
    
    <nav class="navbar">
        <h1 className="h1-navbar">Benvenuto in ExoBank</h1>{navBar()}
    </nav>

    {visualSideBar()}
    
    
    
    
    
    
    
    </>
)





}
