import { useState } from "react"
import { utenteLogin } from "../service/utenteService";
import { useDispatch } from 'react-redux';
import { INDEX_ADMIN, INDEX_UTENTE, REGISTRAZIONE } from "../utility/Route";
import { useHistory } from 'react-router-dom/cjs/react-router-dom';
import { setUtente } from "../store/slice/utenteSlice";
function Home() {

  const [email, setEmail] = useState();
  const [password, setPassword] = useState();
  const dispatch = useDispatch()
  const history = useHistory();



  function login() {
    if (email && email !== null && password && password !== null) {
      const utente = {
        email: email,
        password: password
      }
      console.log(email);
      console.log(password);
      utenteLogin(utente).then((response) => {
        console.log("response java")
        console.log(response)
        if (response.data) {
          dispatch(setUtente(response.data))
          if (response.data.ruolo.id === 2) {
            history.push(INDEX_UTENTE)
          } else history.push(INDEX_ADMIN)

        }
      }).catch(error => {
        alert("Credenziali Errate")
      })

    } else alert("Inserisci le credenziali di accesso")
  }




  return (

    <>
      <div className="div">
        <div className="div-container">
          <h2>Utente Login</h2>

          <input type="text" placeholder="Email" onChange={(e) => setEmail(e.target.value)} />
          <input type="password" placeholder="Password" onChange={(e) => setPassword(e.target.value)} />
          <button type="button" onClick={() => login()}>Entra</button>

          <button type="submit" onClick={(e) => history.push(REGISTRAZIONE)} >Registrati</button>
        </div>
      </div>


    </>




  )



} export default Home
