import { useEffect, useState } from "react"
import { listaStatiTransazione, listaTransazioniAdmin, updateTransazione } from "../service/adminService"
import { useHistory } from "react-router-dom/cjs/react-router-dom";
import { ERRORE, INDEX_ADMIN } from "../utility/Route";
import { useSelector } from "react-redux";



export default function ListaTransazioniAdmin({ setErrore }) {

    const [statiTransazione, setStatiTransazione] = useState();
    const [listaTransazioni, setListaTransazioni] = useState();
    const [aggiornamento, setAggiornamento] = useState(true)
    const history = useHistory();
    const [stato, setStato] = useState();
    const utente = useSelector(state => state.utente);





    useEffect(() => {

        if (utente.ruolo) {
            if (utente.ruolo.id == 1) {

            } else {
                const errore = {
                    titoloErrore: "ACCESSO NON AUTORIZZATO",
                    descrizioneErrore: "Non hai i permessi per visualizzare la pagina"
                }
                setErrore(errore);
                history.push(ERRORE)
            }
        } else {
            const errore = {
                titoloErrore: "UTENTE NON RICONOSCIUTO",
                descrizioneErrore: "Effettua il login"
            }
            setErrore(errore);
            history.push(ERRORE)
        }

        listaStatiTransazione().then((response) => {
            if (response.data) {
                setStatiTransazione(response.data)
            }
        })

        listaTransazioniAdmin().then((response) => {
            if (response.data.success) {
                setListaTransazioni(response.data.data)
            }
        })
    }, [aggiornamento])



    function modificaStato(transazione) {
        console.log(stato)
        transazione.statoTransazione.id = stato
        console.log(transazione.id)
        console.log(transazione.statoTransazione.id)
        updateTransazione(transazione).then((response) => {
            if (response.data) {
                setAggiornamento(!aggiornamento)
                alert("Transazione aggiornata con successo")
            } else alert("errore")

        }).catch(error => {
            console.log(error)
        })
    }


    function formattaData(dataLista) {
        console.log(dataLista)
        const dataFormattata = new Date(dataLista);
        const giorno = dataFormattata.getDate();
        const mese = dataFormattata.getMonth() + 1;
        const anno = dataFormattata.getFullYear();
        return `${giorno}/${mese}/${anno}`;
    }


    return (


        <>

            <div className="div">

                <div className="table-container">
                    <div className="div-h1">
                        <h3 style={{ fontSize: 20, color: "white" }}><b>LISTA TRANSAZIONI IN CORSO</b></h3>
                    </div>
                    <table>
                        <thead>
                            <tr>
                                <th>NOME</th>
                                <th>COGNOME</th>
                                <th>CODICE FISCALE</th>
                                <th>NUMERO CONTO</th>
                                <th>SALDO</th>
                                <th>STATO CONTO CORRENTE</th>
                                <th>TIPO TRANSAZIONE</th>
                                <th>DATA TRANSAZIONE</th>
                                <th>IMPORTO TRANSAZIONE</th>
                                <th>STATO TRANSAZIONE</th>
                                <th>OPERAZIONE</th>
                                <th>CONFERMA</th>
                            </tr>
                        </thead>
                        <tbody>

                            {listaTransazioni ? (
                                <>
                                    {listaTransazioni.map((transazione) => {
                                        if (transazione.statoTransazione.id === 3) {
                                            return (
                                                <tr key={transazione.id}>
                                                    <td>{transazione.conto.utente.nome}</td>
                                                    <td>{transazione.conto.utente.cognome}</td>
                                                    <td>{transazione.conto.utente.codiceFiscale}</td>
                                                    <td>{transazione.conto.numeroConto}</td>
                                                    <td>€ {transazione.conto.saldo.toFixed(2)}</td>
                                                    <td>{transazione.conto.stato.statoContoCorrente}</td>
                                                    <td>{transazione.tipo.tipoTransazione}</td>
                                                    <td>{formattaData(transazione.dataTransazione)}</td>
                                                    <td>€ {transazione.importo.toFixed(2)}</td>
                                                    <td>{transazione.statoTransazione.statoTransazione}</td>
                                                    <td>
                                                        <select
                                                            style={{ marginTop: 10, margin: 5 }}
                                                            onChange={(e) => setStato(e.target.value)}
                                                        >
                                                            <option value="">Seleziona uno stato</option>
                                                            {statiTransazione ? (
                                                                <>
                                                                    {statiTransazione.map((s) => (
                                                                        <option key={s.id} value={s.id}>
                                                                            {s.statoTransazione}
                                                                        </option>
                                                                    ))}
                                                                </>
                                                            ) : (
                                                                <>
                                                                </>
                                                            )}
                                                        </select>
                                                    </td>
                                                    <td>
                                                        <button
                                                            type="button"
                                                            onClick={() => modificaStato(transazione)}
                                                        >
                                                            CONFERMA
                                                        </button>
                                                    </td>
                                                </tr>
                                            );
                                        } else {
                                            return null;
                                        }
                                    })}
                                </>
                            ) : (
                                <>
                                </>
                            )}

                        </tbody>
                    </table>

                </div>
                <div className="table-container">
                    <div className="div-h1">
                        <h3 style={{ fontSize: 20, color: "white" }}><b>LISTA TRANSAZIONI</b></h3>
                    </div>
                    <table>
                        <thead>
                            <tr>
                                <th>NOME</th>
                                <th>COGNOME</th>
                                <th>CODICE FISCALE</th>
                                <th>NUMERO CONTO</th>
                                <th>SALDO</th>
                                <th>STATO CONTO CORRENTE</th>
                                <th>TIPO TRANSAZIONE</th>
                                <th>DATA TRANSAZIONE</th>
                                <th>IMPORTO TRANSAZIONE</th>
                                <th>STATO TRANSAZIONE</th>
                            </tr>
                        </thead>
                        <tbody>

                            {listaTransazioni ? (
                                <>
                                    {listaTransazioni.map((transazione) => {
                                        if (transazione.statoTransazione.id !== 3) {
                                            return (
                                                <tr key={transazione.id}>
                                                    <td>{transazione.conto.utente.nome}</td>
                                                    <td>{transazione.conto.utente.cognome}</td>
                                                    <td>{transazione.conto.utente.codiceFiscale}</td>
                                                    <td>{transazione.conto.numeroConto}</td>
                                                    <td>€ {transazione.conto.saldo.toFixed(2)}</td>
                                                    <td>{transazione.conto.stato.statoContoCorrente}</td>
                                                    <td>{transazione.tipo.tipoTransazione}</td>
                                                    <td>{transazione.dataTransazione}</td>
                                                    <td>€ {transazione.importo.toFixed(2)}</td>
                                                    <td>{transazione.statoTransazione.statoTransazione}</td>
                                                </tr>
                                            );
                                        } else {
                                            return null;
                                        }
                                    })}
                                </>
                            ) : (
                                <>
                                </>
                            )}

                        </tbody>
                    </table>
                </div>
            </div>

            <button className="button-1" style={{ marginTop: 30, height: 40 }} type="button" onClick={(e) => history.push(INDEX_ADMIN)} >Vai al menu</button>



        </>
    )
}
