import { useState, useEffect } from "react"
import { listaConti, listaStatiConto, updateConto } from "../service/adminService"
import axios from "axios";
import { URL_LISTA_CONTI } from "../utility/Endpoint";
import { findConto } from "../service/utenteService";
import { ERRORE, INDEX_ADMIN, LISTA_CONTI } from "../utility/Route";
import { useHistory } from "react-router-dom/cjs/react-router-dom";
import { useSelector } from "react-redux";



export default function ListaContiAdmin({setErrore,setPaginaTabelle}) {


    const utente = useSelector(state => state.utente);
    const [conti, setConti] = useState();
    const [stati, setStati] = useState();
    const [id_stato, setId_Stato] = useState();
    const [aggiornamento,setAggiornamento]= useState(true)
    const history = useHistory();
    const [visibile,setVisibile] = useState(false)
    const [visibile2,setVisibile2] = useState(false)
    const [messaggio,setMessaggio]= useState("")

    
    useEffect(() => {

        if(utente.ruolo){
            if (utente.ruolo.id == 1) {

            } else {
                const errore = {
                    titoloErrore: "ACCESSO NON AUTORIZZATO",
                    descrizioneErrore: "Non hai i permessi per visualizzare la pagina"
                }
                setErrore(errore);
                history.push(ERRORE)
            }
        } else {
            const errore = {
                titoloErrore: "UTENTE NON RICONOSCIUTO",
                descrizioneErrore: "Effettua il login"
            }
            setErrore(errore);
            history.push(ERRORE)
        }


        listaConti().then((response) => {
            if(response.data.success){
                console.log("ok")
                console.log("response.data")
                setConti(response.data.data)
                setId_Stato("")
            }
        })
        listaStatiConto().then((response) => {
            setStati(response.data)
        })

        setId_Stato("")

    }, [aggiornamento])

    function reindirizza(rotta) {
        setPaginaTabelle(false)
        history.push(rotta)
    }  

    function modificaStato(conto) {
        conto.stato.id = id_stato
        updateConto(conto).then( (response) => {
            if(response.data){
                setAggiornamento(!aggiornamento)
                setVisibile2(true)
                setMessaggio("Conto numero: "+conto.numeroConto+" aggiornato con successo")   
            }else{
                setAggiornamento(!aggiornamento)
                setVisibile(true)
                setMessaggio("C'è stato un errore durante l'operazione, contatta l'amministratore") 
            }
            
        })
    }

    function errorBanner(messaggio) {
        if (visibile) {
          setTimeout(() => {
            setVisibile(false);
            setMessaggio("");
          }, 5000);
          return (
            <div style={{ color: "red" }} className="error-banner">
              {messaggio}
            </div>
          );
        } else if (visibile2) {
          setTimeout(() => {
            setVisibile2(false);
            setMessaggio("");
          }, 5000);
          return (
            <div style={{ color: "green" }} className="error-banner">
              {messaggio}
            </div>
          );
        } else {
          return null;
        }
      }




    return (
        <>
        <div className="div">
        
        <div className="table-container">
        {errorBanner(messaggio)}
                    <div className="div-h1">
                        <h3 style={{ fontSize: 20, color: "white" }}><b>LISTA CONTI</b></h3>
                    </div>
                <table>
                    <thead>
                        <tr>
                            <th>Numero Conto</th>
                            <th>Saldo</th>
                            <th>Stato Conto</th>
                            <th>Codice cliente</th>
                            <th>Nome cliente</th>
                            <th>Cognome cliente</th>
                            <th>Codice Fiscale</th>
                            <th>Operazione</th>
                            <th>Conferma</th>
                        </tr>
                    </thead>
                    <tbody>

                        {conti ? (
                            conti.map((conto) => (
                                <tr key={conto.id}>
                                    <td>{conto.numeroConto}</td>
                                    <td>€ {conto.saldo}</td>
                                    <td>{conto.stato.statoContoCorrente}</td>
                                    <td>{conto.utente.id}</td>
                                    <td>{conto.utente.nome}</td>
                                    <td>{conto.utente.cognome}</td>
                                    <td>{conto.utente.codiceFiscale}</td>
                                    <td>
                                        <select
                                            onChange={(e) =>
                                                setId_Stato(e.target.value)} >
                                            <option value={id_stato}>Seleziona uno stato</option>
                                            {stati ? (
                                                stati.map((stato) => (
                                                    <option key={stato.id} value={stato.id}>
                                                        {stato.statoContoCorrente}
                                                    </option>
                                                ))
                                            ) : (
                                                <>
                                                </>
                                            )}
                                        </select>
                                    </td>
                                    <td>
                                        <button type="button" style={{backgroundColor:"#1b2c79"}} onClick={() => modificaStato(conto)}>✓</button>
                                    </td>
                                </tr>
                            ))
                        ) : (
                            <>
                            </>
                        )}

                    </tbody>
                </table>
                <button className="button-1" style={{ marginLeft:700,height: 40 }}  type="button" onClick={(e) => reindirizza(INDEX_ADMIN)} >Vai al menu</button>
            </div>
        </div>
            

        </>
    )
}
