                        </tr>
                    </thead>
                    <tbody>

                        {listaTransazioni ? (
                            <>
                                {listaTransazioni.map((transazione) => {
                                    if (transazione.statoTransazione.id !== 4) {
                                        return (
                                            <tr key={transazione.id}>
                                                <td>{transazione.conto.utente.id}</td>
