import axios from "axios"
import { ULR_DEPOSITO, ULR_PRELIEVO, ULR__FIND_CONTO_UTENTE, URL_INSERT_CONTO, URL_LOGIN, URL_REGISTRAZIONE } from "../utility/Endpoint"

/*async: Questa parola chiave viene utilizzata prima di una funzione per dichiararla come asincrona.
 Le funzioni asincrone possono contenere l'operatore await. Quando una funzione è dichiarata come asincrona, 
 questa restituirà sempre una promessa implicitamente.

await: Questa parola chiave può essere utilizzata all'interno di una funzione asincrona per "attendere" il completamento di una promessa.
 Quando si utilizza await su una promessa, la funzione asincrona si "blocca" finché la promessa non viene risolta o rigettata. 
 Questo consente di scrivere codice asincrono in uno stile più simile al codice sincrono.
*/

export async function utenteLogin(utente){
    return await axios.post(URL_LOGIN,utente)
}
export async function apriConto(utente){
    return await axios.post(URL_INSERT_CONTO,utente)
}
export async function registrazione(utente){
    return await axios.post(URL_REGISTRAZIONE,utente)
}
export async function findContoUtente(utente){
    return await axios.post(ULR__FIND_CONTO_UTENTE,utente)
}
export async function deposito(transazione){
    return await axios.post(ULR_DEPOSITO,transazione)
}
export async function prelievo(transazione){
    return await axios.post(ULR_PRELIEVO,transazione)
}
